interface PathProps {
  id: string;
  d: string;
  transform?: string;
  selectedCutId: string | null;
  onCutHover: (cutId: string | null) => void;
}
const PathForId: React.FC<PathProps> = ({id, d, transform, selectedCutId, onCutHover}) => {
  return <path
          id={id}
          d={d}
          fill={selectedCutId===id ? "#FF6A13" : "#f51633"}
          fillOpacity={1}
          transform={transform}
          stroke="#000000"
          strokeWidth={1.00000012}
          strokeMiterlimit={4}
          strokeOpacity={1}
          strokeDasharray="none"
          onMouseEnter={() => onCutHover(id)}
          onMouseLeave={() => onCutHover(null)}
        />
}

